import { subjects } from "@/data";

<template>
  <div class="test-dictation">
    <h1>Test de dictée</h1>
    <h1>{{ $t("welcome") }}</h1>
    <div>
      <label for="difficulty">Niveau de difficulté :</label>
      <select id="difficulty" v-model="selectedDifficulty">
        <option value="facile">Facile</option>
        <option value="moyen">Moyen</option>
        <option value="difficile">Difficile</option>
      </select>
    </div>

    <div>
      <label for="language">Langue :</label>
      <select id="language" v-model="selectedLanguage">
        <option value="en-US">English</option>
        <option value="fr-FR">Français</option>
        <!-- Ajoutez d'autres options pour les autres langues si nécessaire -->
      </select>
    </div>

    <div v-if="filteredSubjects.length > 0">
      <label for="subject">Sujet :</label>
      <select id="subject" v-model="selectedSubject">
        <option value="" disabled selected>Sélectionnez un sujet</option>
        <option v-for="subject in filteredSubjects" :key="subject.title" :value="subject">{{ subject.title }}</option>
      </select>

      <div class="subject-actions">
        <button @click="createSubject">Nouveau sujet</button>
        <button @click="updateSubject">Mettre à jour</button>
        <button @click="deleteSubject">Supprimer</button>
      </div>
    </div>

    <div v-if="selectedSubject">
      <h2>{{ selectedSubject.title }}</h2>
      <button @click="startDictation">Commencer la dictée</button>

      <div v-if="isDictationStarted">
        <p>{{ currentText }}</p>
        <textarea v-model="userInput"></textarea>
        <button @click="checkAnswer">Vérifier</button>
        <button @click="speakText(selectedSubject.text)">Lire le texte</button>
        <div>
          <p>Temps restant : {{ timeRemaining }}s</p>
        </div>

        <h2>{{ selectedSubject.title }}</h2>
        <button @click="startDictation">{{ $t("startDictation") }}</button>
        <div v-for="phrase in selectedSubject.phrases" :key="phrase.text">
           <p>{{ phrase.text }}</p>
           <input type="number" v-model="phrase.repetitions" min="1">
        </div>
        <button @click="startGeneralRepeat">{{ $t("startGeneralRepeat") }}</button>

        <div>
          <p>Répétitions restantes : {{ maxRepetitions - currentRepetition }}</p>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  data() {
    return {
      selectedLanguage: 'fr-FR', // Remplacez 'fr-FR' par la langue de votre choix
      maxRepetitions: 3, // Nombre maximal de répétitions autorisées
      currentRepetition: 1,
      timer: null,
      timeLimit: 60, // Temps limite en secondes
      timeRemaining: 0,

      subjects: [
        {
          title: "Sujet 1",
          difficulty: "facile",
          text: "Le test de dictée est un excellent moyen d'améliorer ses compétences en orthographe."
        },
        {
          title: "Sujet 2",
          difficulty: "moyen",
          text: "La dictée est un exercice classique souvent utilisé dans les écoles pour évaluer les capacités des élèves."
        },
        {
          title: "Sujet 3",
          difficulty: "difficile",
          text: "La vitesse de lecture et la précision sont des aspects importants lors d'un test de dictée."
},
],
selectedDifficulty: "",
selectedSubject: null,
isDictationStarted: false,
currentText: "",
userInput: ""
};
},

computed: {
filteredSubjects() {
if (this.selectedDifficulty) {
return this.subjects.filter(subject => subject.difficulty === this.selectedDifficulty);
} else {
return this.subjects;
}
}
},
      methods: {
      createSubject() {
      const newSubject = {
      title: "Nouveau sujet",
      difficulty: "",
      text: "", 
      };
      this.subjects.push(newSubject);
      this.selectedSubject = newSubject;
      },
      updateSubject() {
      // Implémentez la logique pour mettre à jour le sujet sélectionné
      },
      deleteSubject() {
      // Implémentez la logique pour supprimer le sujet sélectionné
      },
      startDictation() {
      this.currentRepetition = 1;
      this.timeRemaining = this.timeLimit;
      this.timer = setInterval(() => {
      if (this.timeRemaining > 0) {
      this.timeRemaining--;
      } else {
      clearInterval(this.timer);
      this.checkAnswer();
      }
      }, 1000);
      this.isDictationStarted = true;
      this.currentText = this.selectedSubject.text;
      },
      speakText(text, repetitions) {
  const sentences = text.split('.'); // Diviser le texte en phrases en utilisant le point comme séparateur
  const regex = /^[A-Z].*?\.$/; // Expression régulière pour vérifier si une phrase commence par une majuscule et se termine par un point

  let index = 0; // Indice de la phrase actuelle

  const speakNextSentence = () => {
    if (index >= sentences.length) {
      // Toutes les phrases ont été lues
      if (repetitions > 1) {
        this.speakGeneralRepeat(text, repetitions); // Effectuer la répétition générale du texte
      }
      return;
    }

    const sentence = sentences[index].trim();

    if (sentence !== '' && regex.test(sentence)) {
      // La phrase est valide, la lire
      const utterance = new SpeechSynthesisUtterance(sentence);
      utterance.lang = this.selectedLanguage;

      utterance.onend = () => {
        if (index === sentences.length - 1) {
          // Dernière phrase de la lecture
          if (repetitions > 1) {
            this.speakText(text, repetitions - 1); // Répéter la lecture pour le nombre de répétitions restant
          } else {
            this.speakGeneralRepeat(text, repetitions); // Effectuer la répétition générale du texte
          }
        } else {
          index++;
          speakNextSentence(); // Passer à la phrase suivante
        }
      };

      speechSynthesis.speak(utterance);
    } else {
      index++;
      speakNextSentence(); // Passer à la phrase suivante
    }
  };

  speakNextSentence(); // Commencer la lecture de la première phrase
},

speakGeneralRepeat(text, repetitions) {
  const utterance = new SpeechSynthesisUtterance(text);
  utterance.lang = this.selectedLanguage;

  utterance.onend = () => {
    if (repetitions > 1) {
      this.speakGeneralRepeat(text, repetitions - 1); // Effectuer la répétition générale du texte
    }
  };

  speechSynthesis.speak(utterance);
},

      checkAnswer() {
      const userAnswer = this.userInput.trim();
      const expectedAnswer = this.selectedSubject.text.trim();
        if (userAnswer === expectedAnswer) {
          alert("Bonne réponse !");
        } else {
          alert("Mauvaise réponse. Réessayez !");
        }

        if (this.currentRepetition < this.maxRepetitions) {
          this.currentRepetition++;
          this.isDictationStarted = true;
        } else {
          // ...
        }

        // Réinitialiser les valeurs
        this.isDictationStarted = false;
        this.currentText = "";
        this.userInput = "";
      }
      }
      };
</script>
<style scoped>
.test-dictation {
  max-width: 500px;
  margin: 0 auto;
  padding: 20px;
}

h1,
h2 {
  text-align: center;
}

select,
button {
  margin-bottom: 10px;
}

textarea {
  width: 100%;
  height: 100px;
  margin-bottom: 10px;
}

/* ... */
.timer {
  text-align: center;
}

.repetitions {
  text-align: center;
}
</style>
