import VueSpeech from "vue-speech";
<template>
  <div class="test-dictation">
    <h1>Test de dictée</h1>
    <div>
      <label for="difficulty">Niveau de difficulté :</label>
      <select id="difficulty" v-model="selectedDifficulty">
        <option value="facile">Facile</option>
        <option value="moyen">Moyen</option>
        <option value="difficile">Difficile</option>
      </select>
    </div>
    <div v-if="filteredSubjects.length > 0">
  <label for="subject">Sujet :</label>
  <select id="subject" v-model="selectedSubject">
    <option value="" disabled selected>Sélectionnez un sujet</option>
    <option v-for="subject in filteredSubjects" :key="subject.title" :value="subject">{{ subject.title }}</option>
  </select>

  <div class="subject-actions">
    <button @click="createSubject">Nouveau sujet</button>
    <button @click="updateSubject">Mettre à jour</button>
    <button @click="deleteSubject">Supprimer</button>
  </div>
</div>

    <div v-if="selectedSubject">
    <h2>{{ selectedSubject.title }}</h2>
    <button @click="startDictation">Commencer la dictée</button>
    <div v-if="isDictationStarted">
      <p>{{ currentText }}</p>
      <textarea v-model="userInput"></textarea>
      <button @click="checkAnswer">Vérifier</button>
      <button @click="speakText">Lire le texte</button>
    </div>
  </div>
  </div>
</template>

<script>

export default {
  data() {
  return {
    timer: null,
    timeLimit: 60, // Temps limite en secondes
    timeRemaining: 0
    subjects: [
      {
        title: "Sujet 1",
        difficulty: "facile",
        text: "Le test de dictée est un excellent moyen d'améliorer ses compétences en orthographe."
      },
      {
        title: "Sujet 2",
        difficulty: "moyen",
        text: "La dictée est un exercice classique souvent utilisé dans les écoles pour évaluer les capacités des élèves."
      },
      {
        title: "Sujet 3",
        difficulty: "difficile",
        text: "La vitesse de lecture et la précision sont des aspects importants lors d'un test de dictée."
      }
    ],
    selectedDifficulty: "",
    selectedSubject: null,
    isDictationStarted: false,
    currentText: "",
    userInput: ""
  };
},
computed: {
  filteredSubjects() {
    if (this.selectedDifficulty) {
      return this.subjects.filter(subject => subject.difficulty === this.selectedDifficulty);
    } else {
      return this.subjects;
    }
  }
},

  methods: {

     createSubject() {
    const newSubject = {
      title: "Nouveau sujet",
      difficulty: "",
      text: ""
    }
    this.subjects.push(newSubject);
    this.selectedSubject = newSubject;
  },

  updateSubject() {
    // Implémentez la logique pour mettre à jour le sujet sélectionné
  },

  deleteSubject() {
    // Implémentez la logique pour supprimer le sujet sélectionné
  },

    startDictation() {
      this.isDictationStarted = true;
      this.currentText = this.selectedSubject.text;
    },
    speakText() {
      const textToSpeak = this.selectedSubject.text;
      this.$speech.say(textToSpeak);
    },
    checkAnswer() {
      const userAnswer = this.userInput.trim();
      const expectedAnswer = this.selectedSubject.text.trim();

      if (userAnswer === expectedAnswer) {
        alert("Bonne réponse !");
      } else {
        alert("Mauvaise réponse. Réessayez !");
      }

      // Réinitialiser les valeurs
      this.isDictationStarted = false;
      this.currentText = "";
      this.userInput = "";
    }
  }
}


</script>
<style scoped>
.test-dictation {
  max-width: 500px;
  margin: 0 auto;
  padding: 20px;
}

h1, h2 {
  text-align: center;
}

select, button {
  margin-bottom: 10px;
}

textarea {
  width: 100%;
  height: 100px;
  margin-bottom: 10px;
}
</style>
